data.query:
query ExpiringItems(
  $storeId: String
  $itemCodeOrName: String
  $expiryDate: String
) {
  stockLines(
    storeId: $storeId
    filter: {
      itemCodeOrName: { like: $itemCodeOrName }
      expiryDate: { beforeOrEqualTo: $expiryDate }
      hasPacksInStore: true
    }
    sort: { key: itemName, desc: false }
    page: { first: 5000 }
  ) {
    ... on StockLineConnector {
      nodes {
        batch
        expiryDate
        id
        packSize
        storeId
        totalNumberOfPacks
        supplierName
        location {
          code
        }
        item {
          code
          name
          unitName
          stats(storeId: $storeId) {
            averageMonthlyConsumption
          }
        }
      }
    }
  }
}

query InvoiceQuery(
  $storeId: String!
  $dataId: String!
  $sort: PrintReportSortInput
) {
  invoice(storeId: $storeId, id: $dataId) {
    ... on InvoiceNode {
      id
      otherPartyId
      comment
      status
      invoiceNumber
      theirReference
      createdDatetime
      pickedDatetime
      shippedDatetime
      deliveredDatetime
      allocatedDatetime
      otherPartyName
      type
      pricing {
        serviceTotalAfterTax
        serviceTotalBeforeTax
        stockTotalAfterTax
        stockTotalBeforeTax
        taxPercentage
        totalAfterTax
        totalBeforeTax
      }
      otherParty(storeId: $storeId) {
        name
        id
        isSupplier
        isCustomer
        code
        address1
        address2
      }
      user {
        username
      }
    }
    ... on NodeError {
      __typename
      error {
        description
      }
    }
  }
  invoiceLines(storeId: $storeId, invoiceId: $dataId, reportSort: $sort) {
    ... on InvoiceLineConnector {
      nodes {
        id
        itemId
        itemCode
        itemName
        locationName
        item {
          unitName
        }
        packSize
        expiryDate
        batch
        numberOfPacks
        packSize
        sellPricePerPack
        costPricePerPack
        pricing {
          serviceTotalAfterTax
          serviceTotalBeforeTax
          stockTotalAfterTax
          stockTotalBeforeTax
          taxPercentage
          totalAfterTax
          totalBeforeTax
        }
        location {
          code
        }
      }
    }
  }
  store(id: $storeId) {
    ... on StoreNode {
      code
      storeName
      logo
      id
      name(storeId: $storeId) {
        address1
        address2
        chargeCode
        code
        comment
        country
        email
        name
        phone
        website
      }
    }
  }
}

