data.query:
query StocktakeDetailQuery(
  $storeId: String!
  $dataId: String!
  $sort: PrintReportSortInput
) {
  stocktake(storeId: $storeId, id: $dataId) {
    ... on StocktakeNode {
      id
      stocktakeDate
      stocktakeNumber
      status
      createdDatetime
      description
    }
    ... on NodeError {
      __typename
      error {
        description
      }
    }
  }
  stocktakeLines(storeId: $storeId, stocktakeId: $dataId, reportSort: $sort) {
    ... on StocktakeLineConnector {
      nodes {
        id
        itemId
        item {
          ... on ItemNode {
            code
            name
            unitName
          }
        }
        packSize
        location {
          code
        }
        expiryDate
        countedNumberOfPacks
        batch
        snapshotNumberOfPacks
        costPricePerPack
        reasonOption {
          ... on ReasonOptionNode {
            reason
          }
        }
      }
    }
  }
  store(id: $storeId) {
    ... on StoreNode {
      code
      storeName
      logo
      id
      name(storeId: $storeId) {
        address1
        address2
        code
        email
        phone
      }
    }
  }
}

