data.query:
query ExpiringItems(
  $storeId: String
  $itemCodeOrName: String
  $expiryDate: String
) {
  stockLines(
    storeId: $storeId
    filter: {
      itemCodeOrName: { like: $itemCodeOrName }
      expiryDate: { beforeOrEqualTo: $expiryDate }
      hasPacksInStore: true
      isActive: true
    }
    page: { first: 5000 }
  ) {
    ... on StockLineConnector {
      nodes {
        batch
        expiryDate
        id
        packSize
        storeId
        totalNumberOfPacks
        supplierName
        location {
          code
        }
        item {
          code
          name
          unitName
          stats(storeId: $storeId) {
            averageMonthlyConsumption
          }
        }
      }
    }
  }
}

query StockStatus(
  $storeId: String!
  $itemCodeOrName: String
  $categoryId: String
  $isActive: Boolean
) {
  items(
    storeId: $storeId
    filter: {
      codeOrName: { like: $itemCodeOrName }
      categoryId: $categoryId
      isActive: $isActive
      isVisibleOrOnHand: true
    }
    page: { first: 1000 }
    sort: [{ key: name }]
  ) {
    ... on ItemConnector {
      totalCount
      nodes {
        id
        code
        name
        unitName
        type
        isVaccine
        stats(storeId: $storeId) {
          totalConsumption
          averageMonthlyConsumption
          availableStockOnHand
          stockOnHand
          availableMonthsOfStockOnHand
          monthsOfStockOnHand
        }
        availableBatches(storeId: $storeId) {
          ... on StockLineConnector {
            totalCount
            nodes {
              id
              expiryDate
              batch
              totalNumberOfPacks
              availableNumberOfPacks
              packSize
              costPricePerPack
              sellPricePerPack
              onHold
              location {
                code
              }
            }
          }
        }
      }
    }
  }
}

