query encountersQuery(
  $storeId: String!
  $programId: String!
  $after: DateTime
  $before: DateTime
) {
  encounters(
    storeId: $storeId
    filter: {
      programId: { equalTo: $programId }
      status: { equalTo: "PENDING" }
      startDatetime: { afterOrEqualTo: $after, beforeOrEqualTo: $before }
    }
    sort: { key: "startDatetime", desc: false }
  ) {
    ... on EncounterConnector {
      nodes {
        ... on EncounterNode {
          id
          startDatetime
          type
          patient {
            firstName
            lastName
            dateOfBirth
            phone
            nextOfKinName
          }
          programEnrolment {
            programEnrolmentId
          }
          previousEncounter {
            startDatetime
          }
        }
      }
    }
  }
  store(id: $storeId) {
    ... on StoreNode {
      code
      storeName
      logo
      name(storeId: $storeId) {
        ... on NameNode {
          address1
          address2
          chargeCode
          code
          comment
          country
          email
          name
          phone
          website
        }
      }
    }
  }
}

